const { signal, state, stateKeyEnum } = require('./signal');
const mockResponseData = require('./mock/mockRemoteDataResponse.json');

const favouriteAction = (id, signal) => {
  return {
    id,
    signal
  };
};

const feedImage = (src, alt) => {
  return {
    src,
    alt
  };
};

const feedCaption = (text) => {
  return {
    text
  }
};

const feedFavourite = (id) => {
  return {
    align: 'LEFT',
    icon: 'data:image/svg+xml;base64,',
    action: favouriteAction(id, signal(`signal-${id}`))
  }
};

const feedColumn = (count, id) => {
  return {
    columns: [
      feedFavourite(id),
      {
        align: 'RIGHT',
        count,
        signal: {
          id: `signal-${id}`,
          states: [
            state(stateKeyEnum.OK, 22),
            state(stateKeyEnum.ERROR, count)
          ]
        }
      }
    ]
  }
}

const feedItemFactory = (id, caption, image, alt, count) => {
  return {
    items: [
      feedImage(image, alt),
      feedColumn(count, id),
      feedCaption(caption)
    ]
  }
};

const fetchFeed = () => {
  const data = mockResponseData

  const feedList = data.map(({id, caption, image, alt, count}) => feedItemFactory(id, caption, image, alt, count));

  return feedList
};

module.exports = {
  fetchFeed
};