const stateKeyEnum = {
  OK: "OK",
  ERROR: "ERROR"
};

const state = (key, value) => {
  return {
    key,
    value
  }
}

const signal = (id) => {
  return {
    id,
    states: [
      state(stateKeyEnum.OK, "data:image/svg+xml;base64,"),
      state(stateKeyEnum.ERROR, "Failed! Something went wrong")
    ]
  }
}

module.exports = {
  signal,
  state,
  stateKeyEnum
}